mapper.select:
select a.id id, a.doll_id dollId, a.strongVoltage,a.weakOneVoltage,a.weakTwoVoltage, a.strongTime,a.weakTime, a.created_date createdDate, a.created_by createdBy, a.modified_date modifiedDate, a.modified_by modifiedBy , b.name name,b.machine_code machineCode from machine_physical_probability a LEFT JOIN t_doll b on a.doll_id = b.id where 1=1
and b.name like CONCAT('%',#{name},'%')
and a.doll_id =#{dollId}
and b.machine_code = #{machineCode}
order by id desc
