mapper.select:
select a.id, member_id AS memberId, prepaid_amt AS prepaidAmt, a.coins coins, charge_date AS chargeDate, type, charge_method AS chargeMethod, doll_id AS dollId, coins_before AS coinsBefore, coins_after AS coinsAfter from t_member_charge_history a where 1=1
and a.member_id = #{condition}
ORDER by id DESC

mapper.update:
UPDATE account
SET
coins = coins + #{coinsSum}

	,superTicket = superTicket + #{superTicketSum}

WHERE
id = #{memberId}
