mapper.select:
select a.id id, a.game_num gameNum,a.member_id memberId, a.member_num memberNum,a.doll_id dollId, a.member_catch_id memberCatchId, a.complaint_reason complaintReason,a.check_state checkState,a.check_reason checkReason, a.creat_date creatDate,a.modify_date modifyDate, b.name memberName, b.mobile memberPhone,b.register_channel complaintChannel, c.tbimg_real_path dollImg, c.name dollName,c.price dollCions, d.catch_date memberCatchDate, d.catch_status memberCatchResult,d.video_url videoURL from member_complaint a LEFT JOIN t_member b on a.member_id=b.id LEFT JOIN t_doll c on a.doll_id=c.id LEFT JOIN t_doll_catch_history d on a.member_catch_id=d.id where 1=1 AND a.check_state = 0
and a.member_num = #{memberNum,jdbcType=VARCHAR}
and b.register_channel = #{complaintChannel,jdbcType=VARCHAR}
order by a.id desc

select a.id id, a.game_num gameNum,a.member_id memberId, a.member_num memberNum,a.doll_id dollId, a.member_catch_id memberCatchId, a.complaint_reason complaintReason,a.check_state checkState,a.check_reason checkReason, a.creat_date creatDate,a.modify_date modifyDate, b.name memberName, b.mobile memberPhone,b.register_channel complaintChannel, c.tbimg_real_path dollImg, c.name dollName,c.price dollCions, d.catch_date memberCatchDate, d.catch_status memberCatchResult,d.video_url videoURL from member_complaint a LEFT JOIN t_member b on a.member_id=b.id LEFT JOIN t_doll c on a.doll_id=c.id LEFT JOIN t_doll_catch_history d on a.member_catch_id=d.id where 1=1 AND a.check_state != 0
and a.member_num = #{memberNum,jdbcType=VARCHAR}
and b.register_channel = #{complaintChannel,jdbcType=VARCHAR}
order by a.id desc
